import React, { useState } from 'react';
import axios from 'axios';


function Comment(props) {
  const [comment,setComment]=useState();

  const userId=localStorage.getItem('userId');
  const handleSubmit=(e)=>{
    e.preventDefault();    
    axios.post('http://localhost/project9/PHP/insertComment.php?userId='+props.user_id+'&postId='+props.post_id+'&comment='+comment);
  };

  return (
    <>
            {/* <!-- comments
        ================================================== --> */}
        <div className="comments-wrap">

<div id="comments" className="row">
    <div className="column">

        <h3>{comments.length} Comments</h3>

        {/* <!-- START commentlist --> */}
        <ol className="commentlist">

            {comments ? comments.map(comment =>{
                return(
                    <li className="depth-1 comment" key={comment.id}>

                    <div className="comment__avatar">
                        <img className="avatar" src="images/avatars/user-01.jpg" alt="" width="50" height="50" />
                    </div>

                    <div className="comment__content">

                        <div className="comment__info">
                            <div className="comment__author">{comment.name}</div>

                            <div className="comment__meta">
                                <div className="comment__time">{comment.created_at}</div>
                                <div className="comment__reply">
                                </div>
                            </div>
                        </div>

                        <div className="comment__text">
                        <p>{comment.comment}</p>
                        <button className='btn xxs' onClick={(e)=>{e.preventDefault()}}>edit</button>
                        <form action="" style={{display: editDis}} key={comment.id}>
                        <input type="text xs"
                         onChange={(e)=>{setNewComment(e.target.value)}}
                         />
                        <button className='btn xxs ' 
                        key={comment.id}
                        onClick={(e)=>{changeComment(e,comment.id)}}
                        >edit comment</button>
                        </form>
                        </div>

                    </div>

                </li>
                )
            }) : ""}
            
        </ol>

     </div> {/* <!-- end col-full --> */}
 </div> {/* <!-- end comments --> */}

    <div className="row comment-respond">

    {/* <!-- START respond --> */}
    <div id="respond" className="column">
        <h3>Add Comment</h3>
    <form name="commentForm" id="commentForm" method="post" action="" autoComplete="off">

        <div className="form-floating">
            <textarea className="form-control border-0 bg-light" placeholder="Leave a comment here" id="comment" style={{height: "150px"}} name="comment" onChange={(e)=>{setComment(e.target.value)}}></textarea>
            <label htmlFor="comment">Comment</label>
        </div>

        <input name="submit" id="submit" className="btn btn--primary btn-wide btn--large h-full-width" value="Add Comment" type="submit" onClick={handleSubmit}/>

    </form> 
    </div>
 

 </div> 
 </div> 
    </>
  )
}

export default Comment;
